INSERT INTO `cms_article` VALUES ('45', '和小人', '<p><span style=\"color: rgb(102, 102, 102); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; text-align: center; background-color: rgb(255, 255, 255);\">采用国际标准化的网站运维外包模式，用一流的互联网团队为企业提供持续、高品质的内容、设计、技术运营维护和搜索引擎优化（SEO）等服务。</span></p>', '', '安徽易活网络技术公司', '/62\\45.html', '是啦快点金坷垃就看到啦了；速度卡了', '0', '1549');
