  `PEOPLE_DATETIME` datetime NOT NULL COMMENT '注册时间',
  `PEOPLE_APP_ID` int(11) NOT NULL COMMENT '用户所属用户ID',
  `PEOPLE_MAIL` varchar(120) default NULL COMMENT '用户邮箱',
  `PEOPLE_STATE` int(2) default '0' COMMENT '用户状态',
