INSERT INTO `cms_article` VALUES ('90', '', '<p style=\"margin-left:24px\"><span style=\"font-family:宋体;font-size:14px;background:rgb(0,255,255);background:rgb(0,255,255)\">1.&nbsp;</span><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">S</span><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">truts2的介绍:基于mvc的web框架.</span></p><p><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">2.mvc的定义:控制器丶模型丶视图.一直设计典型.</span></p><p><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">3.struts2是webWork继承而来.</span></p><p><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">4.</span><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">struts.properties</span></p><p style=\"text-indent:28px\"><span style=\";font-family:宋体;font-size:16px\">是struts2框架的全局属性文件,自动加载.(键值对).</span></p><p><span style=\";font-family:宋体;font-size:16px;background:rgb(0,255,255);background:rgb(0,255,255)\">5.web.xml</span></p><p><span style=\"font-family: Consolas;font-size: 19px\">&nbsp;&nbsp;&nbsp;</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span></p><p><span style=\"font-family: Consolas;font-size: 19px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter-name</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span><span style=\"font-family: Consolas;font-size: 19px\">struts2</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;/</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter-name</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span></p><p><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter-class</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span><span style=\"font-family: Consolas;font-size: 19px\">org.apache.struts2.dispatcher.ng.filter.StrutsPrepareAndExecuteFilter</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;/</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter-class</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span></p><p><span style=\"font-family: Consolas;font-size: 19px\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;/</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span></p><p><span style=\";font-family:Consolas;color:rgb(63,95,191);font-size:19px\">&lt;!--&nbsp;<span style=\"font-family:宋体\">项目第一次加载</span><span style=\"font-family:Consolas\">web.xml&nbsp;--&gt;</span></span></p><p><span style=\"font-family: Consolas;font-size: 19px\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt;</span><span style=\";font-family:Consolas;color:rgb(63,127,127);font-size:19px\">filter-mapping</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&gt;</span></p><p><span style=\"font-family: Consolas;font-size: 19px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\";font-family:Consolas;color:rgb(0,128,128);font-size:19px\">&lt